final float sumOfSquaredWeights(Searcher searcher) throws IOException {
  for (int i = 0; i < terms.size(); i++)	  // sum term IDFs
    idf += Similarity.idf((Term)terms.elementAt(i), searcher);

  weight = idf * boost;
  return weight * weight;			  // square term weights
}

