public boolean isReplicaInRecoveryHandling(String replicaUrl) {
  boolean exists = false;
  synchronized (replicasInLeaderInitiatedRecovery) {
    exists = replicasInLeaderInitiatedRecovery.containsKey(replicaUrl);
  }
  return exists;
}

