private static void reorder(int[] array, int off, int len, int[] histogram, int shift, int[] dest, int destOff) {
  for (int i = 0; i < len; ++i) {
    final int v = array[off + i];
    final int b = (v >>> shift) & 0xFF;
    dest[destOff + histogram[b]++] = v;
  }
}

