public void reloadCore() throws IOException, ParserConfigurationException, SAXException {
  SolrCore old = core;
  if (multiCore != null) {
    multiCore.create(this);
  } else {
    SolrConfig cfg = new SolrConfig(old.getConfigResource());
    core = new SolrCore(null, null, cfg, null, this);
  }
  old.close(); 
}

