@Override
public boolean isLocked() throws IOException {
  boolean isLocked = false;
  FileSystem fs = FileSystem.newInstance(lockPath.toUri(), conf);
  try {
    isLocked = fs.exists(new Path(lockPath, lockName));
  } finally {
    IOUtils.closeQuietly(fs);
  }
  return isLocked;
}

