/** helper for generating MultiPhraseQueries */
public static Term[] ta(String[] s) {
  Term[] t = new Term[s.length];
  for (int i = 0; i < s.length; i++) {
    t[i] = new Term(FIELD, s[i]);
  }
  return t;
}

