commits.sourceNew:
public DirectSource(IndexInput input, Type type) {
  super(type);
  this.data = input;
  baseOffset = input.getFilePointer();
  switch (type) {
  case FIXED_INTS_16:
    toNumeric = new ShortToLong();
    break;
  case FLOAT_32:
  case FIXED_INTS_32:
    toNumeric = new IntToLong();
    break;
  case FIXED_INTS_8:
    toNumeric = new ByteToLong();
    break;
  default:
    toNumeric = new LongToLong();
  }
}

