public void setScorer(Scorer scorer) throws IOException {
  this.scorer = scorer;
  for (GroupHead groupHead : groups.values()) {
    groupHead.scorer = scorer;
    for (FieldComparator comparator : groupHead.comparators) {
      comparator.setScorer(scorer);
    }
  }
}

