  final int numFields = byNumber.size();
  for(int i=0;i<numFields;i++) {
    final FieldInfo fi = fieldInfo(i);
    if (fi.isIndexed && !fi.omitTermFreqAndPositions) {
      return true;
    }
  }
  return false;
}

