private XPath getXpath() {
  try {
    XPath xpath = xpathTL.get();
    if (xpath == null) {
      xpath = XPathFactory.newInstance().newXPath();
      xpathTL.set(xpath);
    }
    return xpath;
  } catch (Exception e) {
    throw new RuntimeException(e);
  }
}

