/** Write vInt into offsets stream of current
 *  PostingVector */
public void writeOffsetVInt(int i) {
  int upto = 0;
  while ((i & ~0x7F) != 0) {
    writeOffsetByte((byte)((i & 0x7f) | 0x80));
    i >>>= 7;
  }
  writeOffsetByte((byte) i);
}

