/**
 * @deprecated separate norms are not supported in >= 4.0
 */
@Deprecated
boolean hasSeparateNorms() {
  if (normGen == null) {
    return false;
  } else {
    for (long fieldNormGen : normGen.values()) {
      if (fieldNormGen >= YES) {
        return true;
      }
    }
  }

  return false;
}

