@Override
public ValueSourceScorer getRangeScorer(LeafReaderContext readerContext, String lowerVal, String upperVal, boolean includeLower, boolean includeUpper) {
  // TODO: are lowerVal and upperVal in indexed form or not?
  lowerVal = lowerVal == null ? null : toTerm(lowerVal);
  upperVal = upperVal == null ? null : toTerm(upperVal);

  int lower = Integer.MIN_VALUE;
  if (lowerVal != null) {
    lower = termsIndex.lookupTerm(new BytesRef(lowerVal));
    if (lower < 0) {
      lower = -lower-1;
    } else if (!includeLower) {
      lower++;
    }
  }

  int upper = Integer.MAX_VALUE;
  if (upperVal != null) {
    upper = termsIndex.lookupTerm(new BytesRef(upperVal));
    if (upper < 0) {
      upper = -upper-2;
    } else if (!includeUpper) {
      upper--;
    }
  }

  final int ll = lower;
  final int uu = upper;

  return new ValueSourceScorer(readerContext, this) {
    @Override
    public boolean matches(int doc) {
      int ord = termsIndex.getOrd(doc);
      return ord >= ll && ord <= uu;
    }
  };
}

