 * Remove the root Scorer from subScorers and re-establish it as a heap
 */
protected final void minheapRemoveRoot() {
  if (nrInHeap == 1) {
    subScorers[0] = null;
    nrInHeap = 0;
  } else {
    subScorers[0] = subScorers[nrInHeap-1];
    subScorers[nrInHeap-1] = null;
    nrInHeap--;
    minheapAdjust(0);
  }
}

