public static EchoParamStyle get( String v ) {
  if( v != null ) {
    v = v.toUpperCase();
    if( v.equals( "EXPLICIT" ) ) {
      return EXPLICIT;
    }
    if( v.equals( "ALL") ) {
      return ALL;
    }
    if( v.equals( "NONE") ) {  // the same as nothing...
      return NONE;
    }
  }
  return null;
}

