@Override
public Date parseAndAddGap(Date value, String gap) throws java.text.ParseException {
  final DateMathParser dmp = new DateMathParser(DateField.UTC, Locale.US);
  dmp.setNow(value);
  return dmp.parseMath(gap);
}

