static List<Map<String, Object>> getListOfMap(String key, Map<String, Object> jsonMap) {
  Object o = jsonMap.get(key);
  if (o != null) {
    if (!(o instanceof List)) o = singletonList(o);
    return (List) o;
  } else {
    return Collections.emptyList();
  }
}

