protected void validate() throws IllegalStateException {
  if (period < 1) {
    log.info("Turning off shard reporter, period=" + period);
  }
  if (filters.isEmpty()) {
    filters = DEFAULT_FILTERS;
  }
  // start in inform(...) only when core is available
}

