@Override
public int docFreq(String field, BytesRef t) throws IOException {
  ensureOpen();
  int total = 0;          // sum freqs in segments
  for (int i = 0; i < subReaders.length; i++) {
    total += subReaders[i].docFreq(field, t);
  }
  return total;
}

