public void close() throws IOException {
  // DecRef old files from the last checkpoint, if any:
  int size = lastFiles.size();
  if (size > 0) {
    for(int i=0;i<size;i++)
      decRef((List) lastFiles.get(i));
    lastFiles.clear();
  }

  deletePendingFiles();
}

