  assertTrue(w.afterWasCalled);
  w.close();

  IndexReader ir = IndexReader.open(dir, true);
  assertEquals(1, ir.maxDoc());
  assertEquals(0, ir.numDocs());
  ir.close();

  dir.close();
}

