        { currentTokType = BOLD;  yybegin(THREE_SINGLE_QUOTES_STATE);
        }
      case 59: break;
      case 1: 
        { numWikiTokensSeen = 0;  positionInc = 1;
        }
      case 60: break;
      case 33: 
        { positionInc = 1; return HOST;
        }
      case 61: break;
