public void testSetIndex() {
  CharArrayIterator ci = new CharArrayIterator();
  ci.setText("test".toCharArray(), 0, "test".length());
  try {
    ci.setIndex(5);
    fail();
  } catch (Exception e) {
    assertTrue(e instanceof IllegalArgumentException);
  }
}

