/**
 * Removes any written temporary files.
 */
@Override
public void close() throws IOException {
  boolean success = false;
  try {
    closeWriter();
    success = true;
  } finally {
    if (success) {
      IOUtils.deleteFilesIfExist(input, sorted);
    } else {
      IOUtils.deleteFilesIgnoringExceptions(input, sorted);
    }
  }
}

