public Token next(final Token reusableToken) throws IOException {
  assert reusableToken != null;
  if (tokens.size() > 0) {
    return (Token)tokens.removeFirst();
  }

  Token nextToken = input.next(reusableToken);
  if (nextToken == null) {
    return null;
  }

  decompose(nextToken);

  if (tokens.size() > 0) {
    return (Token)tokens.removeFirst();
  } else {
    return null;
  }
}

