        part2 = DateTools.dateToString(d2, dateRes);
      }
    } catch (Exception e) {
      // do nothing
    }

    lower.setText(part1);
    upper.setText(part2);

    return new RangeQueryNode(lower, upper, collator, rewriteMethod);

  }

  return node;

}

