// Returns false if we are the only remaining refs of
// this reader:
public synchronized boolean anyOutsideRefs(SegmentReader sr) {
  int myRefCounts = 0;
  if (sr == reader) {
    myRefCounts++;
  }
  if (sr == mergeReader) {
    myRefCounts++;
  }
  final int rc = sr.getRefCount();
  assert rc >= myRefCounts;
  return rc > myRefCounts;
}

