synchronized boolean nrtIsCurrent(SegmentInfos infos) {
  if (!infos.equals(segmentInfos)) {
    // if any structural changes (new segments), we are
    // stale
    return false;
  } else {
    return !docWriter.anyChanges();
  }
}

