/**
 * Create tokens from the matches in a matcher 
 */
public static List<Token> group( Matcher matcher, String input, int group, CharStream stream )
{
  ArrayList<Token> matchList = new ArrayList<Token>();
  while(matcher.find()) {
    Token t = newToken( stream,
      matcher.group(group), 
      matcher.start(group), 
      matcher.end(group) );
    matchList.add( t );
  }
  return matchList;
}

