private Filter getOldBitSetFilter(final Filter filter) {

  return new Filter() {
    public BitSet bits(IndexReader reader) throws IOException {
      BitSet bits = new BitSet(reader.maxDoc());
      DocIdSetIterator it = filter.getDocIdSet(reader).iterator();
      while(it.next()) {
        bits.set(it.doc());
      }
      return bits;
    }
  };
}

