/** How many bytes is this cache using? */
public synchronized long ramBytesUsed() {
  long bytes = 0;
  for(CachedOrds ords : ordsCache.values()) {
    bytes += RamUsageEstimator.sizeOf(ords);
  }

  return bytes;
}

