@Override
protected Query getBooleanQuery(List<BooleanClause> clauses, boolean disableCoord) throws SyntaxError {
  Query q = super.getBooleanQuery(clauses, disableCoord);
  if (q != null) {
    q = QueryUtils.makeQueryable(q);
  }
  return q;
}

