      coreProvider.withCore(c -> reload(pkg, c));

    }

    @Override
    public MapWriter getPackageVersion(PluginInfo.ClassName cName) {
      return pkgVersion == null ? null : ew -> pkgVersion.writeMap(ew);
    }

  });
}

