    float sumOfSquaredWeights(Searcher searcher)
	throws IOException
    {
	Iterator i = _termArrays.iterator();
	while (i.hasNext())
	{
	    Term[] terms = (Term[])i.next();
	    for (int j=0; j<terms.length; j++)
		_idf += Similarity.idf(terms[j], searcher);
	}

	_weight = _idf * boost;
	return _weight * _weight;
    }

