/**
 * Create tokens from the matches in a matcher 
 */
public static List<Token> group( Matcher matcher, String input, int group )
{
  ArrayList<Token> matchList = new ArrayList<Token>();
  while(matcher.find()) {
    Token t = new Token( 
      matcher.group(group), 
      matcher.start(group), 
      matcher.end(group) );
    matchList.add( t );
  }
  return matchList;
}

/**
 * @deprecated
 */
public static List<Token> group( Matcher matcher, String input, int group ){
  return group(matcher, input, group, null);
}

