public StreamExpressionParameter toExpression(StreamFactory factory){
  StringBuilder sb = new StringBuilder();

  sb.append(leftField);

  if(!leftField.equals(rightField)){
    sb.append("=");
    sb.append(rightField); 
  }

  sb.append(" ");
  sb.append(order);

  return new StreamExpressionValue(sb.toString());
}

