@Override
public String strVal(int doc) {
  int ord=termsIndex.getOrd(doc);
  if (ord==0) return null;
  termsIndex.lookup(ord, spare);
  spareChars.reset();
  ByteUtils.UTF8toUTF16(spare, spareChars);
  return spareChars.toString();
}

