public static List<Map> captureErrors(List<CommandOperation> ops){
  List<Map> errors = new ArrayList<>();
  for (CommandOperation op : ops) {
    if(op.hasError()) {
      errors.add(op.errorDetails());
    }
  }
  return errors;
}

