public void seek(long pos)
    throws IOException
{
    if (pos > length)
        throw new IOException("seeking past end of file");

    if ((pos >>> DbIndexOutput.BLOCK_SHIFT) !=
        (position >>> DbIndexOutput.BLOCK_SHIFT))
    {
        block.seek(pos);
        block.get(directory);
    }

    position = pos;
}

