public float sumOfSquaredWeights() throws IOException {
  Iterator i = termArrays.iterator();
  while (i.hasNext()) {
    Term[] terms = (Term[])i.next();
    for (int j=0; j<terms.length; j++)
      idf += searcher.getSimilarity().idf(terms[j], searcher);
  }

  value = idf * getBoost();
  return value * value;
}

