@SuppressWarnings("static-access")
public Option[] getOptions() {
  return new Option[]{
      OptionBuilder
          .withArgName("path")
          .hasArg()
          .isRequired(true)
          .withDescription("Path to create")
          .create("path"),
      OptionBuilder
          .withArgName("HOST")
          .hasArg()
          .isRequired(true)
          .withDescription("Address of the Zookeeper ensemble; defaults to: " + ZK_HOST)
          .create("zkHost")
  };
}

