/**
 * Returns default buffer sizes for the given {@link IOContext}
 */
public static int bufferSize(IOContext context) {
  switch (context.context) {
  case DEFAULT:
  case FLUSH:
  case READ:
    return BUFFER_SIZE;
  case MERGE:
    return MERGE_BUFFER_SIZE;
  default:
    assert false : "unknown IOContext " + context.context;
    return BUFFER_SIZE;
  }
}

