ScheduledTrigger(AutoScaling.Trigger trigger, SolrCloudManager cloudManager, Stats stats) throws IOException {
  this.trigger = trigger;
  this.queue = new TriggerEventQueue(cloudManager, trigger.getName(), stats);
  this.replay = true;
  this.isClosed = false;
}

