/**
 * Restart all configured servers, i.e. configuration will be re-read
 */
public static void restartServers() {
  servers.slist.forEach(s -> {
    try {
      s.stop();
      s.start();
    } catch (Exception e) {
      fail("Exception during server restart: " + e.getMessage());
    }
  });
}

