public void writeAttr(String name, String val, boolean escape) throws IOException{
  if (val != null) {
    writer.write(' ');
    writer.write(name);
    writer.write("=\"");
    if(escape){
      XML.escapeAttributeValue(val, writer);
    } else {
      writer.write(val);
    }
    writer.write('"');
  }
}

