/** Returns true if any fields have freqs */
public boolean hasFreq() {
  if (isReadOnly()) {
    return hasFreq;
  }
  // mutable FIs must check!
  for (FieldInfo fi : this) {
    if (fi.isIndexed && fi.indexOptions != IndexOptions.DOCS_ONLY) {
      return true;
    }
  }
  return false;
}

