@Override
public boolean exists(String path) {
  String fullPath = new File(path).getAbsolutePath();
  synchronized (this) {
    CacheValue cacheValue = byPathCache.get(fullPath);
    Directory directory = null;
    if (cacheValue != null) {
      directory = cacheValue.directory;
    }
    if (directory == null) {
      return false;
    } else {
      return true;
    }
  }
}

