private DocumentBuilder getXmlDocumentBuilder() {
  try {
    DocumentBuilder builder = builderTL.get();
    if (builder == null) {
      builder = DocumentBuilderFactory.newInstance().newDocumentBuilder();
      builderTL.set(builder);
    }
    return builder;
  } catch (Exception e) {
    throw new RuntimeException(e);
  }
}

