@Override
@After
public void tearDown() throws Exception {
  super.tearDown();
  masterJetty.stop();
  slaveJetty.stop();
  masterJetty = slaveJetty = null;
  master = slave = null;
  masterClient.close();
  slaveClient.close();
  masterClient = slaveClient = null;
  System.clearProperty("solr.indexfetcher.sotimeout");
}

