    private static void rmDir(File dirName) throws Exception {
        if (dirName.exists()) {
            if (dirName.isDirectory()) {
                File[] contents = dirName.listFiles();
                for (int i = 0; i < contents.length; i++)
                    rmDir(contents[i]);
                dirName.delete();
            } else {
                dirName.delete();
            }
        }
	}

